// components/session/Login.jsx
import React, { useState } from 'react';
import fire from '../fire';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [signUpEmail, setsignUpEmail] = useState("");
    const [signUpPassword, setSignUpPassword] = useState("");
    
    function handleSignUp(e) {
      e.preventDefault();

      if (password.length < 6) {
        alert("Please enter more than 6 characters for a password");
        return;
      }
      fire.auth().createUserWithEmailAndPassword(signUpEmail, signUpPassword)
      .then(console.log("you've signed up mate"))
      //.then(cred =>{
        //store.dispatch(currentUser(cred.user.uid))
        setEmail("")
        setPassword("")

        //})
        // .catch(err => {
        //   console.log(err.message)
        // })
    } 

    const handleSignIn = (e) => {
        e.preventDefault();
        fire.auth().signInWithEmailAndPassword(email, password)
        .then(console.log("all good mate"))
          .catch((error) => {
            console.error('Incorrect username or password');
          });}

    return (
        <div>    
        <h2>Login</h2>
            <form onSubmit={handleSignIn}>
                <input
                    type="text"
                    onChange={({ target }) =>     
                      setEmail(target.value)}
                    placeholder="Email"
                />
                <br />
                <input
                    type="password"
                    onChange={({ target}) => 
                      setPassword(target.value)}
                    placeholder="Password"
                />
                <br />
                <button type="submit">
                    Sign in
                </button>
                
            </form>

            <h2>Sign Up</h2>
            <form onSubmit={handleSignUp}>
                <input
                    type="text"
                    onChange={({ target }) =>     
                      setEmail(target.value)}
                    placeholder="Email"
                />
                <br />
                <input
                    type="password"
                    onChange={({ target}) => 
                      setPassword(target.value)}
                    placeholder="Password"
                />
                <br />
                <button type="submit">
                    Sign up
                </button>
                
            </form>
        </div>
    )
};

export default Login
