import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import {produce} from "immer"

export default function CreateDiaryEntry(){

  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [milestones, setMilestones] = useState([])
  const [date, setdate] = useState(new Date())
  const [goal, setGoal] = useState({
    title: "test1",
    description: "test2",
    date: new Date(),
    milestone: "testMileStone"
  })
 


  function onChangeTitle(e) {
    setTitle(e.target.value)
    setGoal({...goal,  title: e.target.value})
}

  function onChangeDescription(e) {
    setDescription(e.target.value)
    setGoal({...goal,  description: e.target.value})
  }

  function onChangeMilestone(e) {
    setMilestones(parseInt(e.target.value))
    setGoal({...goal,  milestone: parseInt(e.target.value)})
  }

  function onChangeDate(date) {
    setdate(date)
    setGoal({...goal,  date: date})
  }


  function onSubmit(e) {
    e.preventDefault();
    console.log(goal);

    axios.post('http://localhost:5000/goals/add', goal)
      .then(res => console.log(res.data));

    window.location = '/';
  }

    return (
    <div>
      <h3>Create New Goal</h3>
      <form onSubmit={onSubmit}>

        <div className="form-group"> 
          <label htmlFor='title'>Title: </label>
          <input
              type="text"
              id='title'
              name='title'
              required
              className="form-control"
              value={title}
              onChange={onChangeTitle}/>
        </div>

        <div className="form-group"> 
          <label htmlFor='description'>Description: </label>
          <input  
              type="text"
              id='description'
              name='description'
              required
              className="form-control"
              value={description}
              onChange={onChangeDescription}
              />
        </div>

        <div className="form-group">
          <label htmlFor='milestone'>Add a milestone </label>
          <input 
              type="text" 
              id='milestone'
              name='milestone'
              className="form-control"
              value={milestones}
              onChange={onChangeMilestone}
              />
        </div>

        {/* <div className="form-group">
          <label>Completed by: </label>
          <div>
            <DatePicker
              selected={date}
              onChange={onChangeDate}
            />
          </div>
        </div> */}

        <div className="form-group">
          <input type="submit" value="Create goal Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
}
