import React, { useState} from 'react';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

export default function Adduser(){
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [bio, setBio] = useState("");
  const [userName, setUserName] = useState("");
  const [givenName, setGivenName] = useState("");
  const [surname, setSurname] = useState("")
  const [pronoun, setPronoun] = useState("")
  

  function newUserInDB(){
    let newUser = {
      "email":email, "name":name
    }

    try{
      axios.post('http://localhost:5000/testusers/add', newUser)
        .then(res => console.log(res.data));
      }catch(e){
        console.error(e)
      }
  }

  function handleSignUp(e) {
    e.preventDefault();
    newUserInDB()      
  } 

  const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));

  const classes = useStyles();

  return (
    <div>
      
      <div className="container">  
      <h3>Create an account</h3>  
      <h4>{name}</h4>
        <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSignUp}>
            <TextField onChange={({ target }) =>     
                  setName(target.value)} id="standard-basic" label="Standard" />
            <TextField id="filled-basic" label="Filled" variant="filled" />
            <input
                type="text"
                onChange={({ target }) =>     
                  setName(target.value)}
                placeholder="Name"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setEmail(target.value)}
                placeholder="Email"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setBio(target.value)}
                placeholder="Bio"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setUserName(target.value)}
                placeholder="userName"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setGivenName(target.value)}
                placeholder="Given Name"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setSurname(target.value)}
                placeholder="Surname"
            />
            <br />
            <input
                type="text"
                onChange={({ target }) =>     
                  setPronoun(target.value)}
                placeholder="Pronoun"
            />
            <br />
            <button type="submit">
                Sign up
            </button>
        </form>

      </div>
    </div>
  )
}
